import React, { useState } from 'react';
import "@radix-ui/themes/styles.css";
import { IconButton, DropdownMenu } from '@radix-ui/themes';
import { DotsHorizontalIcon } from '@radix-ui/react-icons';
import { User } from '../types/User';
import EditUserModal from '../modals/EditUserModal';
import DeleteUserModal from '../modals/DeleteUserModal';

const ActionMenu: React.FC<any> = ({user}:{user: User}) => { 

    const [openEditUserModal, setOpenEditUserModal] = useState(false);
    const [openDeleteUserModal, setOpenDeleteUserModal] = useState(false);

    const onClose = () => setOpenEditUserModal(false);

    console.log("user", user)
    return (
        <>
            <DropdownMenu.Root>
                <DropdownMenu.Trigger>
                    <IconButton radius="full" variant="ghost">
                        <DotsHorizontalIcon width="18" height="18" />
                    </IconButton>
                </DropdownMenu.Trigger>

                <DropdownMenu.Content>
                    <DropdownMenu.Item shortcut="⌘ E" onClick={() => setOpenEditUserModal(true)}>Edit user</DropdownMenu.Item>
                    <DropdownMenu.Item shortcut="⌘ D">Delete user</DropdownMenu.Item>
                </DropdownMenu.Content>
            </DropdownMenu.Root>


            <EditUserModal 
                user={user}
                isOpen={openEditUserModal}
                onClose={onClose}
            />
            <DeleteUserModal 
                user={user}
                isOpen={openEditUserModal}
                onClose={onClose}
            />
        </>

    )


}

export default ActionMenu;
