const API_BASE_URL = "http://localhost:3002";


interface User {
    createdAt: Date;
    first: string;
    last: string;
    id: string;
    photo: string;
    updatedAt: Date;
    roleId: string;
}

interface Role {
    createdAt: Date;
    name: string;
    id: string;
    isDefault: string;
    updatedAt: Date;
    description: string;
}

export async function getUsers({
    currentPage,
    searchText = "",
  }: {
    currentPage: number;
    searchText: string;
  }) {
    const response = await fetch(
      `${API_BASE_URL}/users?search=${searchText}&page=${currentPage}`,
    );
    if (!response.ok) {
      throw new Error("Error with network response");
    }

    const users = await response.json()
    const roles = await getRoles();

   const userRoles = users.data.map((user: User) => {
      const role = roles.data.find((role: Role) => role.id === user.roleId);
      return { ...user, role };
    })

    console.log("userRoles")
    return { ...userRoles };
}

export async function getRoles() {
    const response = await fetch(
      `${API_BASE_URL}/roles`,
    );
    if (!response.ok) {
      throw new Error("Error with network response");
    }

    const roles = await response.json()

    return { ...roles };
}

export async function getUserRole({
    roleId,
  }: {
    roleId: string;
  }) {
    const response = await fetch(
      `${API_BASE_URL}/roles?id=${roleId}`,
    );
    if (!response.ok) {
      throw new Error("Error with network response");
    }

    const data = await response.json()

    return { ...data };
}
