import React, { useState } from 'react';
import "@radix-ui/themes/styles.css";
import { Box, Skeleton, Table } from '@radix-ui/themes';
import { useRoles } from '../../hooks/api';
import DropDownMenu from './DropdownMenu';

interface RoleResponse {
    createdAt: Date;
    name: string;
    id: string;
    isDefault: string;
    updatedAt: Date;
    description: string;
}

const RolesTable: React.FC<any> = () => { 

    const { data, isLoading, error } = useRoles();

    const roles = data?.data;


    return (
        isLoading ? 
        <Skeleton />
        :
        <Box style={{ border: "2px solid var(--gray-a5)", borderRadius: "var(--radius-4)" }}>
            <Table.Root>
                <Table.Header>
                    <Table.ColumnHeaderCell>Name</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell>Description</Table.ColumnHeaderCell>
                    {/* Catie - add in screen reader label for actions */}
                    <Table.ColumnHeaderCell />
                </Table.Header>

                <Table.Body>
                    {roles?.map((role: RoleResponse) => (
                        <Table.Row>
                        <Table.RowHeaderCell>{role.name}</Table.RowHeaderCell>
                        {/* Catie - add in max width or character cut off */}
                        <Table.Cell>{role.description}</Table.Cell>
                        <Table.Cell>
                            <DropDownMenu role={role.id} />
                        </Table.Cell>
                    </Table.Row>
                    ))} 
                </Table.Body>
            </Table.Root> 
        </Box>
    )
}

export default RolesTable;
