import React, { useState } from 'react';
import "@radix-ui/themes/styles.css";
import {  Table } from '@radix-ui/themes';
import { useQuery } from '@tanstack/react-query';
import { getRoles } from '../until/api';
import ActionMenu from './DropdownMenu';

interface RoleResponse {
    createdAt: Date;
    name: string;
    id: string;
    isDefault: string;
    updatedAt: Date;
    description: string;
}

const RolesTable: React.FC<any> = () => { 
    const [currentPage, setCurrentPage] = useState(1);

    const { data, status } = useQuery({
        queryKey: ["roles"],
        queryFn: () => getRoles({ currentPage }),
    });

    const roles = data?.data;


    return (
        <Table.Root>
            <Table.Header>
                <Table.ColumnHeaderCell>Name</Table.ColumnHeaderCell>
                <Table.ColumnHeaderCell>Description</Table.ColumnHeaderCell>
                {/* Catie - add in screen reader label for actions */}
                <Table.ColumnHeaderCell></Table.ColumnHeaderCell>
            </Table.Header>

            <Table.Body>
            {roles?.map((role: RoleResponse) => (
                        <Table.Row>
                        <Table.RowHeaderCell>{role.name}</Table.RowHeaderCell>
                        {/* Catie - add in max width or character cut off */}
                        <Table.Cell>{role.description}</Table.Cell>
                        <Table.Cell>
                            <ActionMenu user="" />
                        </Table.Cell>
                    </Table.Row>
                    ))} 
            </Table.Body>
        </Table.Root> 

    )


}

export default RolesTable;
