import React, { ChangeEvent, useEffect, useState } from 'react';
import "@radix-ui/themes/styles.css";
import { Table, Box, Button, Flex, Avatar,  Skeleton, TextField } from '@radix-ui/themes';
import DropDownMenu from './DropdownMenu';
import { formatDate } from '../../util/formatDate';
import { User } from '../../types/User';
import PaginationButtons from '../PaginationButtons';
import { useUsers } from '../../hooks/api';
import SkeletonTable from '../SkeletonTable';
import { MagnifyingGlassIcon } from '@radix-ui/react-icons';


const UserTable: React.FC<any> = () => { 

    const [currentPage, setCurrentPage] = useState(1);
    const [hasNextPage, setHasNextPage] = useState(false);
    const [searchText, setSearchText] = useState("");

    // Use loading for skeleton rendering
    const { data, isLoading, error } = useUsers(currentPage, searchText);

    const users = data?.completeUsers;


    // Handle search input changes
    const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        setSearchText(e.target.value);
        // Reset to first page when searching
        setCurrentPage(1);
    };

    console.log("data", users)

     useEffect(() => {
        if (data) {
            // Check if there are more pages
            setHasNextPage(data.pages > currentPage);
            console.log("Data pages:", data.pages, "Current page:", currentPage);
        }
    }, [data, currentPage]);

    return (
    isLoading ? 
            <SkeletonTable /> :  
        <>
        <Box width="732px">

            <TextField.Root 
                size="2" 
                placeholder="Search by nameâ€¦"
                value={searchText}
                onChange={handleSearchChange}
            >
                <TextField.Slot>
                    <MagnifyingGlassIcon height="16" width="16" />
                </TextField.Slot>
            </TextField.Root>
            </Box>

            <Table.Root>
                    <Table.Header style={{ background: "var(--gray-a2)" }}>
                        <Table.ColumnHeaderCell>User</Table.ColumnHeaderCell>
                        <Table.ColumnHeaderCell>Role</Table.ColumnHeaderCell>
                        <Table.ColumnHeaderCell>Joined</Table.ColumnHeaderCell>
                        <Table.ColumnHeaderCell />
                    </Table.Header>

                <Table.Body>
                    {users?.map((user: User) => (
                        <Table.Row>
                            <Table.Cell>
                                <Flex gap="2" align="center">
                                    <Avatar
                                        src={user.photo}
                                        fallback={user.first.charAt(0)}
                                        radius="full"
                                        size="2"
                                    />
                                    {user.first} {user.last}
                                </Flex>
                            </Table.Cell>
                            <Table.Cell>
                                {user.role.name}
                            </Table.Cell>
                            <Table.Cell>

                                {formatDate(user.createdAt)}
                                </Table.Cell>
                            <Table.Cell>
                                <DropDownMenu user={user} />
                            </Table.Cell>
                        </Table.Row>
                    ))} 
                </Table.Body>

            </Table.Root> 

            {data && data.pages > 1 && (
                <PaginationButtons 
                    onPrevious={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    onNext={() => setCurrentPage(prev => hasNextPage ? prev + 1 : prev)}
                    hasPrevious={currentPage > 1}
                    hasNext={hasNextPage} />
            )}
        </>

    )


}

export default UserTable;
