import React, { useEffect, useState } from 'react';
import "@radix-ui/themes/styles.css";
import { Table, Box, Button, Flex, Avatar, Text } from '@radix-ui/themes';
import ActionMenu from './DropdownMenu';
import { formatDate } from '../util/formatDate';
import { User } from '../types/User';
import PaginationButtons from './PaginationButtons';
import { useUsers } from '../hooks/api';


const UserTable: React.FC<any> = ({searchText}:{searchText: string}) => { 

    const [currentPage, setCurrentPage] = useState(1);

    const { data, isLoading, error } = useUsers(currentPage, searchText);


    const users = data?.completeUsers;


    return (
        <>
            <Table.Root>
                <Table.Header style={{ background: "var(--gray-a2)" }}>
                    <Table.ColumnHeaderCell>User</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell>Role</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell>Joined</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell />
                </Table.Header>

                <Table.Body>
                    {users?.map((user: User) => (
                        <Table.Row>
                            <Table.Cell>
                                <Flex gap="2" align="center">
                                    <Avatar
                                        src={user.photo}
                                        fallback={user.first.charAt(0)}
                                        radius="full"
                                        size="2"
                                    />
                                    {user.first} {user.last}
                                </Flex>
                            </Table.Cell>
                            <Table.Cell>
                                {user.role.name}
                            </Table.Cell>
                            <Table.Cell>

                                {formatDate(user.createdAt)}
                                </Table.Cell>
                            <Table.Cell>
                                <ActionMenu user="" />
                            </Table.Cell>
                        </Table.Row>
                    ))} 
                </Table.Body>

            </Table.Root> 

            {/* Catie - show only if there are next pages */}
           <PaginationButtons pageProps={users} />
        </>

    )


}

export default UserTable;
