import React, { useState } from 'react';
import "@radix-ui/themes/styles.css";
import { Table, Box, Button, Flex, Avatar } from '@radix-ui/themes';
import ActionMenu from './DropdownMenu';
import { useQuery } from '@tanstack/react-query';
import { getUsers } from '../until/api';
import { formatDate } from '../until/formatDate';

interface Role {
    createdAt: Date;
    name: string;
    id: string;
    isDefault: string;
    updatedAt: Date;
    description: string;
}

interface UserResponse {
    createdAt: Date;
    first: string;
    last: string;
    id: string;
    role: Role;
    photo: string;
    updatedAt: Date;
    roleId: string;
}

const UserTable: React.FC<any> = () => { 

    const [currentPage, setCurrentPage] = useState(1);
    const [searchText, setSearchText] = useState("");

    const { data } = useQuery({
        queryKey: ["users", currentPage, searchText],
        queryFn: () => getUsers({ currentPage, searchText }),
    });

    const users = data?.completeUsers;

    return (
        <Box style={{ border: "2px solid var(--gray-a5)", borderRadius: "var(--radius-4)" }}>
            <Table.Root>
                <Table.Header style={{ background: "var(--gray-a2)" }}>
                    <Table.ColumnHeaderCell>User</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell>Role</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell>Joined</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell></Table.ColumnHeaderCell>
                </Table.Header>

                <Table.Body>
                    {users?.map((user: UserResponse) => (
                        <Table.Row>
                            <Table.Cell>
                                <Flex gap="2" align="center">
                                    <Avatar
                                        src={user.photo}
                                        fallback={user.first.charAt(0)}
                                        radius="full"
                                        size="2"
                                    />
                                    {user.first} {user.last}
                                </Flex>
                            </Table.Cell>
                            <Table.Cell>
                                {user.role.name}
                            </Table.Cell>
                            <Table.Cell>

                                {formatDate(user.createdAt)}
                                </Table.Cell>
                            <Table.Cell>
                                <ActionMenu user="" />
                            </Table.Cell>
                        </Table.Row>
                    ))} 
                </Table.Body>

                <Flex justify="end" align="end" gap="2" py="2">
                    <Button disabled={currentPage == 1 ? true : false}>
                        Previous
                    </Button>
                    <Button variant='outline' color="gray">
                        <Text>
                            Next
                            </Text>
                    </Button>
                </Flex>
            </Table.Root> 
        </Box> 
    )


}

export default UserTable;
