import React, { useState, ChangeEvent } from 'react';
import "@radix-ui/themes/styles.css";
import { Box, Button, Flex, Tabs, TextField } from '@radix-ui/themes';
import UserTable from '../components/User/UsersTable';
import RolesTable from '../components/Role/RolesTable';
import { MagnifyingGlassIcon } from '@radix-ui/react-icons';
import { useUsers } from '../hooks/api';

const UserRoleManager: React.FC<any> = () => { 

    const [currentPage, setCurrentPage] = useState(1);
    const [searchText, setSearchText] = useState("");


      const { data } = useUsers(currentPage, searchText);


    const users = data?.completeUsers;

    // Handle search input changes
    const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        setSearchText(e.target.value);
        // Reset to first page when searching
        setCurrentPage(1);
    };

    return (
        <Tabs.Root defaultValue="user">
            <Tabs.List>
                <Tabs.Trigger value="user">Users</Tabs.Trigger>
                <Tabs.Trigger value="role">Roles</Tabs.Trigger>
            </Tabs.List>

            <Flex gap="2" my="24px" width="100%">

            <Box width="732px">

            <TextField.Root 
                        size="2" 
                        placeholder="Search by nameâ€¦"
                        value={searchText}
                        onChange={handleSearchChange}
                    >
                <TextField.Slot>
                    <MagnifyingGlassIcon height="16" width="16" />
                </TextField.Slot>
            </TextField.Root>
            </Box>
            {/* Catie - This isn't exactly the right hex for the button - should be purple/9 or #6565EC */}
            <Button color="iris">
                + Add User
            </Button>
            </Flex>

            <Box style={{ border: "2px solid var(--gray-a5)", borderRadius: "var(--radius-4)" }}>
                <Tabs.Content value="user">
                    <UserTable users={users} />
                </Tabs.Content>

                <Tabs.Content value="role">
                    <RolesTable />
                </Tabs.Content>
            </Box>
        </Tabs.Root>
    )
}

export default UserRoleManager;
