import React, { useEffect, useState } from 'react';
import { Role } from '../../types/Role';
import { Button, Dialog, Flex, TextArea, Text, TextField } from '@radix-ui/themes';
import { ChatBubbleIcon } from '@radix-ui/react-icons';
import { User } from '../../types/User';
import { formatDate } from '../../util/formatDate';


const EditUserModal: React.FC<any> = ({user, isOpen, onClose} : {user: User, isOpen: boolean, onClose: () => void}) => {

// Catie - redefine valeus type
  const onSubmit = async (values: any) => {
    return patch(`/api/compensation-verification/set-employment-details/`, {
      body: values,
    }).then((updated: User) => {
      mutate({
        ...data!,
        ...updated,
      });
      //loading spinner + success flag + close monday
      onSubmitSuccess();
    });
  };


  return (
    <Dialog.Root open={isOpen} onOpenChange={onClose}>
      <Dialog.Content maxWidth="450px">
        <Dialog.Title>Edit user</Dialog.Title>
        <Flex direction="column" gap="3">
          <label>
            <Text as="div" size="2" mb="1" weight="bold">
              Name
            </Text>
            <TextField.Root defaultValue={`${user.first} ${user.last}`} />
          </label>
          <label>
            <Text as="div" size="2" mb="1" weight="bold">
              Role
            </Text>
            {/* role could be empty technically */}
            <TextField.Root
              defaultValue={user.role?.name}
              placeholder="Enter role"
            />
          </label>
          <label>
            <Text as="div" size="2" mb="1" weight="bold">
              Created
            </Text>
            <TextField.Root
              defaultValue={formatDate(user.createdAt)}
            />
          </label>
        </Flex>

        <Flex gap="3" mt="4" justify="end">
          <Dialog.Close>
            <Button variant="soft" color="gray">
              Cancel
            </Button>
          </Dialog.Close>
          <Dialog.Close>
            <Button>Save</Button>
          </Dialog.Close>
        </Flex>
      </Dialog.Content>
    </Dialog.Root>
  )
}

export default EditUserModal;
