import React, { useState } from 'react';
import "@radix-ui/themes/styles.css";
import { Box, Flex, Skeleton, Table, TextField } from '@radix-ui/themes';
import { useRoles } from '../../hooks/api';
import DropDownMenu from './DropdownMenu';

interface RoleResponse {
    createdAt: Date;
    name: string;
    id: string;
    isDefault: string;
    updatedAt: Date;
    description: string;
}

const RolesTable: React.FC<any> = () => { 

    const { data, isLoading, error } = useRoles();

    const roles = data?.data;


    // Handle search input changes
    const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
        setSearchText(e.target.value);
        // Reset to first page when searching
        setCurrentPage(1);
    };
    return (
        isLoading ? 
        <Skeleton />
        :
        <>
        <Flex gap="2" my="24px" width="100%">
                <Box width="732px">
                    <TextField.Root 
                        size="2" 
                        placeholder="Search by nameâ€¦"
                        value={searchText}
                        onChange={handleSearchChange}
                    >
                        <TextField.Slot>
                            <MagnifyingGlassIcon height="16" width="16" />
                        </TextField.Slot>
                    </TextField.Root>
                </Box>
                {/* Catie - This isn't exactly the right hex for the button - should be purple/9 or #6565EC */}
                <Button color="iris">
                    + Add User
                </Button>
            </Flex>
        <Box style={{ border: "2px solid var(--gray-a5)", borderRadius: "var(--radius-4)" }}>
            <Table.Root>
                <Table.Header>
                    <Table.ColumnHeaderCell>Name</Table.ColumnHeaderCell>
                    <Table.ColumnHeaderCell>Description</Table.ColumnHeaderCell>
                    {/* Catie - add in screen reader label for actions */}
                    <Table.ColumnHeaderCell />
                </Table.Header>

                <Table.Body>
                    {roles?.map((role: RoleResponse) => (
                        <Table.Row>
                        <Table.RowHeaderCell>{role.name}</Table.RowHeaderCell>
                        {/* Catie - add in max width or character cut off */}
                        <Table.Cell>{role.description}</Table.Cell>
                        <Table.Cell>
                            <DropDownMenu role={role.id} />
                        </Table.Cell>
                    </Table.Row>
                    ))} 
                </Table.Body>
            </Table.Root> 
        </Box>
        </>
    )
}

export default RolesTable;
